server/index.ts.content:
import 'dotenv/config';
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000 for development
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = process.env.PORT || 5000;
  server.listen({
    port,
    host: "0.0.0.0",
  }, () => {
    log(`serving on port ${port}`);
  });
})();

// Export for Vercel serverless functions
export default app;

server/paystack.ts.content:
// Paystack service using direct HTTP API calls
// Based on official Paystack API documentation: https://api.paystack.co/transaction/initialize

export interface PaymentData {
  email: string;
  amount: number; // Amount in kobo (multiply by 100)
  reference?: string;
  callback_url?: string;
  metadata?: Record<string, any>;
  planId?: string;
  packageType?: string;
}

export interface PaymentVerification {
  status: boolean;
  data: {
    id: number;
    domain: string;
    status: string;
    reference: string;
    amount: number;
    message: string | null;
    gateway_response: string;
    paid_at: string;
    created_at: string;
    channel: string;
    currency: string;
    ip_address: string;
    metadata: Record<string, any>;
    log: any;
    fees: number | null;
    fees_split: any | null;
    authorization: {
      authorization_code: string;
      bin: string;
      last4: string;
      exp_month: string;
      exp_year: string;
      channel: string;
      card_type: string;
      bank: string;
      country_code: string;
      brand: string;
      reusable: boolean;
      signature: string;
      account_name: string | null;
    };
    customer: {
      id: number;
      first_name: string;
      last_name: string;
      email: string;
      customer_code: string;
      phone: string;
      metadata: Record<string, any>;
      risk_action: string;
      international_format_phone: string | null;
    };
    plan: any;
    split: any;
    order_id: any;
    paidAt: string;
    createdAt: string;
    requested_amount: number;
    pos_transaction_data: any;
    source: any;
    fees_breakdown: any;
  };
}

export class PaystackService {
  private static readonly PAYSTACK_BASE_URL = 'https://api.paystack.co';
  private static readonly SECRET_KEY = process.env.PAYSTACK_SECRET_KEY;

  // Initialize payment transaction
  static async initializePayment(paymentData: PaymentData) {
    try {
      console.log('=== PAYSTACK SERVICE DEBUG ===');
      console.log('PAYSTACK_SECRET_KEY exists:', !!this.SECRET_KEY);
      console.log('PAYSTACK_SECRET_KEY length:', this.SECRET_KEY?.length || 0);
      console.log('Payment data received:', paymentData);

      // Check if Paystack is properly configured
      if (!this.SECRET_KEY) {
        throw new Error('PAYSTACK_SECRET_KEY not configured. Please set your Paystack secret key in .env file');
      }

      console.log('Using real Paystack API...');
      console.log('Making request to:', `${this.PAYSTACK_BASE_URL}/transaction/initialize`);

      const response = await fetch(`${this.PAYSTACK_BASE_URL}/transaction/initialize`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.SECRET_KEY}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: paymentData.email,
          amount: paymentData.amount,
          reference: paymentData.reference,
          callback_url: paymentData.callback_url,
          metadata: paymentData.metadata,
        }),
      });

      console.log('Paystack API response status:', response.status);
      console.log('Paystack API response headers:', Object.fromEntries(response.headers.entries()));

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Paystack API error response:', errorText);
        throw new Error(`Paystack API error: ${response.status} ${response.statusText} - ${errorText}`);
      }

      const responseData = await response.json();
      console.log('Paystack API response data:', responseData);

      if (!responseData.status) {
        throw new Error(`Paystack API returned error: ${responseData.message || 'Unknown error'}`);
      }

      return {
        success: true,
        data: responseData.data,
      };
    } catch (error) {
      console.error('Paystack initialization error:', error);
      console.error('Error details:', {
        name: error instanceof Error ? error.name : 'Unknown',
        message: error instanceof Error ? error.message : String(error),
        stack: error instanceof Error ? error.stack : 'No stack trace'
      });
      throw error;
    }
  }

  // Verify payment transaction
  static async verifyPayment(reference: string): Promise<PaymentVerification | null> {
    try {
      console.log('=== PAYSTACK VERIFICATION DEBUG ===');
      console.log('Verifying payment with reference:', reference);
      console.log('PAYSTACK_SECRET_KEY exists:', !!this.SECRET_KEY);

      if (!reference || reference.trim() === '') {
        throw new Error('Payment reference is required and cannot be empty');
      }

      // Check if Paystack is properly configured
      if (!this.SECRET_KEY) {
        throw new Error('PAYSTACK_SECRET_KEY not configured. Please set your Paystack secret key in .env file');
      }

      console.log('Making verification request to:', `${this.PAYSTACK_BASE_URL}/transaction/verify/${reference}`);

      const response = await fetch(`${this.PAYSTACK_BASE_URL}/transaction/verify/${reference}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${this.SECRET_KEY}`,
          'Content-Type': 'application/json',
        },
      });

      console.log('Paystack verification response status:', response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Paystack verification error response:', errorText);
        throw new Error(`Paystack verification error: ${response.status} ${response.statusText} - ${errorText}`);
      }

      const responseData = await response.json();
      console.log('Paystack verification response data:', responseData);

      if (!responseData.status) {
        console.log('Paystack API returned status false:', responseData.message);
        throw new Error(`Paystack verification returned error: ${responseData.message || 'Unknown error'}`);
      }

      // Additional validation for successful payments
      if (responseData.data && responseData.data.status !== 'success') {
        console.log('Payment status is not success:', responseData.data.status);
        throw new Error(`Payment not successful. Status: ${responseData.data.status}`);
      }

      console.log('Payment verification successful');
      return responseData;
    } catch (error) {
      console.error('Paystack verification error:', error);
      throw error;
    }
  }

  // Generate payment reference
  static generateReference(): string {
    return `ref_${Date.now()}_${Math.random().toString(36).substring(2, 15)}`;
  }
}

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import multer from "multer";
import path from "path";
import fs from "fs";
import express from "express";
import { PaystackService } from "./paystack";

// Multer configuration for file uploads
const imageStorage = multer.diskStorage({
  destination: (req, file, cb) => {
    cb(null, 'uploads/images/');
  },
  filename: (req, file, cb) => {
    const uniqueSuffix = Date.now() + '-' + Math.round(Math.random() * 1E9);
    cb(null, file.fieldname + '-' + uniqueSuffix + path.extname(file.originalname));
  }
});

const planFileStorage = multer.diskStorage({
  destination: (req, file, cb) => {
    const tier = req.body.tier || 'basic';
    const planId = req.body.planId || 'temp';
    const uploadPath = `uploads/plans/${tier}/`;

    // Create directory if it doesn't exist
    if (!fs.existsSync(uploadPath)) {
      fs.mkdirSync(uploadPath, { recursive: true });
    }

    cb(null, uploadPath);
  },
  filename: (req, file, cb) => {
    const uniqueSuffix = Date.now() + '-' + Math.round(Math.random() * 1E9);
    cb(null, file.fieldname + '-' + uniqueSuffix + path.extname(file.originalname));
  }
});

const uploadImage = multer({ 
  storage: imageStorage,
  fileFilter: (req, file, cb) => {
    if (file.mimetype.startsWith('image/')) {
      cb(null, true);
    } else {
      cb(new Error('Only image files are allowed!'));
    }
  },
  limits: { fileSize: 10 * 1024 * 1024 } // 10MB limit
});

const uploadPlanFile = multer({ 
  storage: planFileStorage,
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['.pdf', '.dwg', '.dxf', '.zip'];
    const fileExt = path.extname(file.originalname).toLowerCase();
    if (allowedTypes.includes(fileExt)) {
      cb(null, true);
    } else {
      cb(new Error('Only PDF, DWG, DXF, and ZIP files are allowed!'));
    }
  },
  limits: { fileSize: 50 * 1024 * 1024 } // 50MB limit
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Test endpoint to verify server is working
  app.get("/api/test", (req, res) => {
    res.json({ message: "Server is running!", timestamp: new Date().toISOString() });
  });

  // Serve static files from uploads directory
  app.use('/uploads', express.static('uploads'));

  // File Upload API
  app.post("/api/upload/image", uploadImage.single('image'), (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ error: 'No file uploaded' });
      }
      res.json({ 
        filename: req.file.filename,
        path: `/uploads/images/${req.file.filename}`,
        url: `/uploads/images/${req.file.filename}`
      });
    } catch (error) {
      console.error("Error uploading image:", error);
      res.status(500).json({ error: "Failed to upload image" });
    }
  });

  app.post("/api/upload/plan-files", uploadPlanFile.fields([
    { name: 'basic', maxCount: 5 },
    { name: 'standard', maxCount: 5 },
    { name: 'premium', maxCount: 5 }
  ]), (req, res) => {
    try {
      const files = req.files as { [fieldname: string]: Express.Multer.File[] };
      const uploadedFiles: { [tier: string]: string[] } = {};

      for (const tier in files) {
        uploadedFiles[tier] = files[tier].map(file => `/uploads/plans/${tier}/${file.filename}`);
      }

      res.json({ files: uploadedFiles });
    } catch (error) {
      console.error("Error uploading plan files:", error);
      res.status(500).json({ error: "Failed to upload plan files" });
    }
  });

  // Plans API
  app.get("/api/plans", async (req, res) => {
    try {
      const { status, featured } = req.query;
      const filters: { status?: string; featured?: boolean } = {};

      if (status) filters.status = status as string;
      if (featured) filters.featured = featured === 'true';

      const plans = await storage.getPlans(filters);
      res.json(plans);
    } catch (error) {
      console.error("Error fetching plans:", error);
      res.status(500).json({ error: "Failed to fetch plans" });
    }
  });

  app.get("/api/plans/:id", async (req, res) => {
    try {
      const plan = await storage.getPlan(req.params.id);
      if (!plan) {
        return res.status(404).json({ error: "Plan not found" });
      }
      res.json(plan);
    } catch (error) {
      console.error("Error fetching plan:", error);
      res.status(500).json({ error: "Failed to fetch plan" });
    }
  });

  app.post("/api/plans", async (req, res) => {
    try {
      const plan = await storage.createPlan(req.body);
      res.status(201).json(plan);
    } catch (error) {
      console.error("Error creating plan:", error);
      res.status(500).json({ error: "Failed to create plan" });
    }
  });

  app.put("/api/plans/:id", async (req, res) => {
    try {
      const plan = await storage.updatePlan(req.params.id, req.body);
      if (!plan) {
        return res.status(404).json({ error: "Plan not found" });
      }
      res.json(plan);
    } catch (error) {
      console.error("Error updating plan:", error);
      res.status(500).json({ error: "Failed to update plan" });
    }
  });

  app.delete("/api/plans/:id", async (req, res) => {
    try {
      const success = await storage.deletePlan(req.params.id);
      if (!success) {
        return res.status(404).json({ error: "Plan not found" });
      }
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting plan:", error);
      res.status(500).json({ error: "Failed to delete plan" });
    }
  });

  // Orders API
  app.get("/api/orders", async (req, res) => {
    try {
      const { userId } = req.query;
      const orders = await storage.getOrders(userId as string);
      res.json(orders);
    } catch (error) {
      console.error("Error fetching orders:", error);
      res.status(500).json({ error: "Failed to fetch orders" });
    }
  });

  app.post("/api/orders", async (req, res) => {
    try {
      const order = await storage.createOrder(req.body);
      res.status(201).json(order);
    } catch (error) {
      console.error("Error creating order:", error);
      res.status(500).json({ error: "Failed to create order" });
    }
  });

  // Profiles API
  app.get("/api/profiles/:userId", async (req, res) => {
    try {
      const profile = await storage.getProfile(req.params.userId);
      if (!profile) {
        return res.status(404).json({ error: "Profile not found" });
      }
      res.json(profile);
    } catch (error) {
      console.error("Error fetching profile:", error);
      res.status(500).json({ error: "Failed to fetch profile" });
    }
  });

  app.post("/api/profiles", async (req, res) => {
    try {
      const profile = await storage.createProfile(req.body);
      res.status(201).json(profile);
    } catch (error) {
      console.error("Error creating profile:", error);
      res.status(500).json({ error: "Failed to create profile" });
    }
  });

  app.put("/api/profiles/:userId", async (req, res) => {
    try {
      const profile = await storage.updateProfile(req.params.userId, req.body);
      if (!profile) {
        return res.status(404).json({ error: "Profile not found" });
      }
      res.json(profile);
    } catch (error) {
      console.error("Error updating profile:", error);
      res.status(500).json({ error: "Failed to update profile" });
    }
  });

  // Analytics API
  app.get("/api/analytics", async (req, res) => {
    try {
      console.log("Analytics endpoint called");
      const analytics = await storage.getAnalytics();
      console.log("Analytics fetched successfully:", analytics);
      res.json(analytics);
    } catch (error) {
      console.error("Error fetching analytics:", error);
      console.error("Error stack:", error instanceof Error ? error.stack : 'No stack trace');
      res.status(500).json({ error: "Failed to fetch analytics", details: error instanceof Error ? error.message : String(error) });
    }
  });

  // User Analytics API
  app.get("/api/analytics/user/:userId", async (req, res) => {
    try {
      const { userId } = req.params;

      // Get user-specific analytics
      const userOrders = await storage.getOrders(userId);
      const userDownloads = await storage.getDownloads(userId);

      const analytics = {
        totalOrders: userOrders.length,
        totalSpent: userOrders.reduce((sum, order) => sum + parseFloat(order.amount), 0),
        totalDownloads: userDownloads.length,
        recentOrders: userOrders.slice(0, 5),
        recentDownloads: userDownloads.slice(0, 5)
      };

      res.json(analytics);
    } catch (error) {
      console.error("Error fetching user analytics:", error);
      res.status(500).json({ error: "Failed to fetch user analytics" });
    }
  });

  // Users API
  app.get("/api/users", async (req, res) => {
    try {
      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ error: "Failed to fetch users" });
    }
  });

  // Authentication API
  app.post("/api/auth/signin", async (req, res) => {
    try {
      const { email, password } = req.body;

      // For the admin user, check credentials
      if (email === 'admin@sakconstructionsgh.com' && password === 'admin123') {
        const profile = await storage.getProfileByEmail(email);
        if (profile) {
          res.json({
            user: { id: profile.user_id, email: profile.email },
            profile
          });
          return;
        }
      }

      // For regular users, check if they exist in the database
      const profile = await storage.getProfileByEmail(email);
      if (profile) {
        // In production, you'd verify the password hash here
        // For now, we'll allow any user with a valid email to sign in
        res.json({
          user: { id: profile.user_id, email: profile.email },
          profile
        });
        return;
      }

      res.status(401).json({ error: "Invalid credentials" });
    } catch (error) {
      console.error("Error signing in:", error);
      res.status(500).json({ error: "Failed to sign in" });
    }
  });

  app.post("/api/auth/signup", async (req, res) => {
    try {
      const { email, password, firstName, lastName } = req.body;

      // Validate required fields
      if (!email || !password) {
        return res.status(400).json({ error: "Email and password are required" });
      }

      // Check if user already exists
      const existingProfile = await storage.getProfileByEmail(email);
      if (existingProfile) {
        return res.status(409).json({ error: "User with this email already exists" });
      }

      // Create new user and profile
      // Generate a proper UUID for the user
      const { randomUUID } = await import('crypto');
      const userId = randomUUID();

      const profileData = {
        user_id: userId,
        email,
        first_name: firstName || null,
        last_name: lastName || null,
        phone: null,
        role: 'user',
        avatar_url: null,
        address: null,
        city: null,
        country: 'Ghana',
        bio: null,
        company: null,
        website: null
      };

      const profile = await storage.createProfile(profileData);

      if (profile) {
        res.status(201).json({
          user: { id: userId, email },
          profile
        });
      } else {
        res.status(500).json({ error: "Failed to create user profile" });
      }

    } catch (error) {
      console.error("Error signing up:", error);
      res.status(500).json({ error: "Failed to sign up" });
    }
  });

  app.post("/api/auth/signout", async (req, res) => {
    res.json({ success: true });
  });

  // Payment routes
  app.post("/api/payments/initialize", async (req, res) => {
    try {
      console.log('=== PAYMENT INITIALIZATION DEBUG ===');
      console.log('Request body received:', req.body);
      console.log('Request headers:', req.headers);

      const { email, amount, planId, planTitle, packageType, userId } = req.body;
      console.log('Extracted fields:', { email, amount, planId, planTitle, packageType, userId });
      console.log('User ID type:', typeof userId);
      console.log('User ID value:', userId);

      if (!email || !amount || !planId) {
        console.log('Missing required fields:', { 
          hasEmail: !!email, 
          hasAmount: !!amount, 
          hasPlanId: !!planId 
        });
        return res.status(400).json({ error: "Missing required payment data" });
      }

      console.log('All required fields present, generating reference...');
      const reference = PaystackService.generateReference();
      console.log('Generated reference:', reference);

      const amountInKobo = Math.round(amount * 100); // Convert cedis to kobo
      console.log('Amount conversion:', { original: amount, inKobo: amountInKobo });

      // Get user ID from request body or generate one
      let requestUserId = userId;

      const paymentData = {
        email,
        amount: amountInKobo,
        reference,
        callback_url: `${req.protocol}://${req.get('host')}/payment/verify`,
        planId,
        packageType,
        metadata: {
          planId,
          planTitle,
          packageType,
          userId: requestUserId, // Store user ID in metadata for verification
          custom_fields: [
            {
              display_name: "Plan",
              variable_name: "plan",
              value: planTitle,
            },
            {
              display_name: "Package",
              variable_name: "package",
              value: packageType,
            },
          ],
        },
      };

      console.log('Payment data prepared:', paymentData);
      console.log('Callback URL:', paymentData.callback_url);
      console.log('Environment check - PAYSTACK_SECRET_KEY exists:', !!process.env.PAYSTACK_SECRET_KEY);
      console.log('Environment check - PAYSTACK_SECRET_KEY length:', process.env.PAYSTACK_SECRET_KEY?.length || 0);

      console.log('Calling PaystackService.initializePayment...');
      const result = await PaystackService.initializePayment(paymentData);
      console.log('PaystackService result:', result);

      if (result.success && result.data) {
        console.log('Payment initialization successful, sending response...');
        res.json({
          success: true,
          authorization_url: result.data.authorization_url,
          access_code: result.data.access_code,
          reference: result.data.reference,
        });
      } else {
        console.log('Payment initialization failed - no success or data');
        res.status(400).json({ error: "Failed to initialize payment" });
      }
    } catch (error) {
      console.error("=== PAYMENT INITIALIZATION ERROR ===");
      console.error("Error type:", typeof error);
      console.error("Error constructor:", error?.constructor?.name);
      console.error("Error message:", (error as any)?.message);
      console.error("Error stack:", (error as any)?.stack);
      console.error("Full error object:", error);

      res.status(500).json({ 
        error: "Failed to initialize payment",
        details: (error as any)?.message || 'Unknown error',
        type: (error as any)?.constructor?.name || 'Unknown'
      });
    }
  });

  app.get("/api/payments/verify/:reference", async (req, res) => {
    try {
      const { reference } = req.params;
      console.log('=== PAYMENT VERIFICATION DEBUG ===');
      console.log('Verifying payment with reference:', reference);

      if (!reference) {
        console.log('No reference provided');
        return res.status(400).json({ error: "Payment reference is required" });
      }

      console.log('Calling PaystackService.verifyPayment...');
      const verification = await PaystackService.verifyPayment(reference);
      console.log('Paystack verification result:', verification);

      if (!verification) {
        console.log('Verification returned null/undefined');
        return res.status(400).json({ error: "Payment verification failed" });
      }

      if (verification.data.status === 'success') {
        console.log('Payment successful, creating order...');
        console.log('Verification metadata:', verification.data.metadata);

        // Validate required metadata
        const metadata = verification.data.metadata || {};
        const planId = metadata.planId;
        const packageType = metadata.packageType;

        if (!planId) {
          console.error('Missing planId in payment metadata:', metadata);
          return res.status(400).json({ 
            error: "Invalid payment: missing plan information",
            details: "Plan ID not found in payment metadata"
          });
        }

        if (!packageType || !['basic', 'standard', 'premium'].includes(packageType)) {
          console.error('Missing or invalid packageType in payment metadata:', metadata);
          return res.status(400).json({ 
            error: "Invalid payment: missing or invalid package type",
            details: "Package type must be basic, standard, or premium"
          });
        }

        // Get user ID from metadata or find/create user
        let orderUserId = metadata.userId;

        if (!orderUserId) {
          console.log('No user ID in metadata, checking if user exists by email...');
          // Try to find user by email from Paystack response
          const userEmail = verification.data.customer?.email;
          if (userEmail) {
            const existingProfile = await storage.getProfileByEmail(userEmail);
            if (existingProfile) {
              orderUserId = existingProfile.user_id;
              console.log('Found existing user by email:', orderUserId);
            } else {
              console.log('No existing user found, creating new user profile...');
              // Create new user profile
              const { randomUUID } = await import('crypto');
              orderUserId = randomUUID();
              const profileData = {
                user_id: orderUserId,
                email: userEmail,
                first_name: verification.data.customer?.first_name || null,
                last_name: verification.data.customer?.last_name || null,
                phone: verification.data.customer?.phone || null,
                role: 'user',
                avatar_url: null,
                address: null,
                city: null,
                country: 'Ghana',
                bio: null,
                company: null,
                website: null
              };
              await storage.createProfile(profileData);
              console.log('Created new user profile:', orderUserId);
            }
          } else {
            console.error('No email found in Paystack response for guest user creation');
            return res.status(400).json({ 
              error: "Invalid payment: no customer email provided",
              details: "Cannot create order without user identification"
            });
          }
        }

        console.log('Final user ID for order:', orderUserId);

        // Verify plan exists before creating order
        const plan = await storage.getPlan(planId);
        if (!plan) {
          console.error('Plan not found:', planId);
          return res.status(400).json({ 
            error: "Invalid payment: plan not found",
            details: `Plan with ID ${planId} does not exist`
          });
        }

        // Create order record
        const orderData = {
          user_id: orderUserId,
          plan_id: planId,
          tier: packageType,
          amount: String(verification.data.amount / 100), // Convert back to cedis
          payment_intent_id: reference,
          status: 'completed',
        };

        console.log('Order data:', orderData);
        const order = await storage.createOrder(orderData);
        console.log('Order created:', order);

        res.json({
          success: true,
          payment: verification.data,
          order,
        });
      } else {
        console.log('Payment not successful, status:', verification.data.status);
        res.status(400).json({
          success: false,
          error: "Payment was not successful",
          status: verification.data.status,
        });
      }
    } catch (error) {
      console.error("=== PAYMENT VERIFICATION ERROR ===");
      console.error("Error type:", typeof error);
      console.error("Error message:", (error as any)?.message);
      console.error("Error stack:", (error as any)?.stack);
      console.error("Full error object:", error);
      res.status(500).json({ error: "Failed to verify payment" });
    }
  });

  app.post("/api/payments/callback", async (req, res) => {
    try {
      // Paystack webhook callback
      const { reference } = req.body;

      if (reference) {
        const verification = await PaystackService.verifyPayment(reference);
        if (verification && verification.data.status === 'success') {
          // Update order status or perform any post-payment actions
          console.log('Payment verified via callback:', reference);
        }
      }

      res.status(200).send('OK');
    } catch (error) {
      console.error("Error handling payment callback:", error);
      res.status(500).send('Error');
    }
  });

  // Download routes
  app.get("/api/downloads", async (req, res) => {
    try {
      const { userId } = req.query;

      if (!userId) {
        return res.status(400).json({ error: "User ID is required" });
      }

      // Get all downloads for a user
      const downloads = await storage.getDownloads(userId as string);

      res.json(downloads);
    } catch (error) {
      console.error("Error fetching downloads:", error);
      res.status(500).json({ error: "Failed to fetch downloads" });
    }
  });

  app.get("/api/downloads/:orderId", async (req, res) => {
    try {
      const { orderId } = req.params;
      const order = await storage.getOrder(orderId);

      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }

      if (order.status !== 'completed') {
        return res.status(403).json({ error: "Payment not completed" });
      }

      const plan = await storage.getPlan(order.plan_id);
      if (!plan) {
        return res.status(404).json({ error: "Plan not found" });
      }

      // Get files based on tier hierarchy: basic = basic only, standard = basic + standard, premium = basic + standard + premium
      let availableFiles: string[] = [];
      const planFiles = plan.plan_files as { basic?: string[]; standard?: string[]; premium?: string[] } || {};

      if (order.tier === 'basic') {
        availableFiles = planFiles.basic || [];
      } else if (order.tier === 'standard') {
        availableFiles = [
          ...(planFiles.basic || []),
          ...(planFiles.standard || [])
        ];
      } else if (order.tier === 'premium') {
        availableFiles = [
          ...(planFiles.basic || []),
          ...(planFiles.standard || []),
          ...(planFiles.premium || [])
        ];
      }

      if (!availableFiles || availableFiles.length === 0) {
        return res.status(404).json({ error: "No files available for download" });
      }

      res.json({
        orderId,
        planTitle: plan.title,
        packageType: order.tier,
        files: availableFiles,
        expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days from now
      });
    } catch (error) {
      console.error("Error fetching download info:", error);
      res.status(500).json({ error: "Failed to fetch download information" });
    }
  });

  app.get("/api/downloads/:orderId/file", async (req, res) => {
    try {
      const { orderId } = req.params;
      const { filePath } = req.query;

      if (!filePath) {
        return res.status(400).json({ error: "File path is required" });
      }

      const order = await storage.getOrder(orderId);

      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }

      if (order.status !== 'completed') {
        return res.status(403).json({ error: "Payment not completed" });
      }

      const plan = await storage.getPlan(order.plan_id);
      if (!plan) {
        return res.status(404).json({ error: "Plan not found" });
      }

      // Get files based on tier hierarchy and verify access
      let availableFiles: string[] = [];
      const planFiles = plan.plan_files || {};

      type PlanFiles = {
        basic?: string[];
        standard?: string[];
        premium?: string[];
        [key: string]: any;
      };
      const typedPlanFiles: PlanFiles = planFiles as PlanFiles;

      if (order.tier === 'basic') {
        availableFiles = typedPlanFiles.basic || [];
      } else if (order.tier === 'standard') {
        availableFiles = [
          ...(typedPlanFiles.basic || []),
          ...(typedPlanFiles.standard || [])
        ];
      } else if (order.tier === 'premium') {
        availableFiles = [
          ...(typedPlanFiles.basic || []),
          ...(typedPlanFiles.standard || []),
          ...(typedPlanFiles.premium || [])
        ];
      }

      if (!availableFiles.includes(filePath as string)) {
        return res.status(403).json({ error: "File not included in your package" });
      }

      const fullFilePath = path.join(process.cwd(), filePath as string);

      if (!fs.existsSync(fullFilePath)) {
        return res.status(404).json({ error: "File not found" });
      }

      // Record the download
      await storage.recordDownload({
        order_id: orderId,
        plan_id: order.plan_id,
        user_id: order.user_id,
        created_at: new Date(),
        download_count: 1,
      });

      // Set appropriate headers for download
      const fileName = path.basename(fullFilePath);
      res.setHeader('Content-Disposition', `attachment; filename="${fileName}"`);
      res.setHeader('Content-Type', 'application/octet-stream');

      // Stream the file
      const fileStream = fs.createReadStream(fullFilePath);
      fileStream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      res.status(500).json({ error: "Failed to download file" });
    }
  });



  // Contact Form Email API
  app.post("/api/contact/send-email", async (req, res) => {
    try {
      const { to, subject, name, email, phone, planType, message } = req.body;

      // Validate required fields
      if (!name || !email || !message) {
        return res.status(400).json({ error: 'Name, email, and message are required' });
      }

      // For now, we'll simulate sending the email
      // In production, you would integrate with a real email service like:
      // - Nodemailer with Gmail SMTP
      // - SendGrid
      // - Mailgun
      // - AWS SES

      console.log('Contact form submission:', {
        to,
        subject,
        name,
        email,
        phone,
        planType,
        message,
        timestamp: new Date().toISOString()
      });

      // Simulate email sending delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      // In production, replace this with actual email sending logic:
      // const emailResult = await sendEmail({
      //   to: 'sakconstructiongh@gmail.com',
      //   subject: subject,
      //   html: `
      //     <h2>New Contact Form Submission</h2>
      //     <p><strong>Name:</strong> ${name}</p>
      //     <p><strong>Email:</strong> ${email}</p>
      //     <p><strong>Phone:</strong> ${phone || 'Not provided'}</p>
      //     <p><strong>Plan Type:</strong> ${planType || 'Not specified'}</p>
      //     <p><strong>Message:</strong></p>
      //     <p>${message}</p>
      //   `
      // });

      res.json({ 
        success: true, 
        message: 'Email sent successfully',
        timestamp: new Date().toISOString()
      });

    } catch (error) {
      console.error("Error sending contact email:", error);
      res.status(500).json({ error: "Failed to send email" });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { users, profiles, plans, orders, downloads } from "@shared/schema";
import type { User, Profile, Plan, Order, Download, InsertUser } from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, sql } from "drizzle-orm";

export interface IStorage {
  // User management
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Profile management
  getProfile(userId: string): Promise<Profile | undefined>;
  getProfileByEmail(email: string): Promise<Profile | undefined>;
  createProfile(profile: Omit<Profile, 'id' | 'created_at' | 'updated_at'>): Promise<Profile>;
  updateProfile(userId: string, updates: Partial<Profile>): Promise<Profile | undefined>;

  // Plans management
  getPlans(filters?: { status?: string; featured?: boolean }): Promise<Plan[]>;
  getPlan(id: string): Promise<Plan | undefined>;
  createPlan(plan: Omit<Plan, 'id' | 'created_at' | 'updated_at'>): Promise<Plan>;
  updatePlan(id: string, updates: Partial<Plan>): Promise<Plan | undefined>;
  deletePlan(id: string): Promise<boolean>;

  // Orders management
  getOrders(userId?: string): Promise<Order[]>;
  getOrder(id: string): Promise<Order | undefined>;
  createOrder(order: Omit<Order, 'id' | 'created_at' | 'updated_at'>): Promise<Order>;
  updateOrder(id: string, updates: Partial<Order>): Promise<Order | undefined>;

  // Downloads management
  getDownloads(userId?: string): Promise<Download[]>;
  recordDownload(download: Omit<Download, 'id' | 'downloaded_at'>): Promise<Download>;
}

export class DatabaseStorage implements IStorage {
  // User methods
  async getUser(id: number): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.username, username)).limit(1);
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const result = await db.insert(users).values(insertUser).returning();
    return result[0];
  }

  // Profile methods
  async getProfile(userId: string): Promise<Profile | undefined> {
    const result = await db.select().from(profiles).where(eq(profiles.user_id, userId)).limit(1);
    return result[0];
  }

  async getProfileByEmail(email: string): Promise<Profile | undefined> {
    const result = await db.select().from(profiles).where(eq(profiles.email, email)).limit(1);
    return result[0];
  }

  async createProfile(profile: Omit<Profile, 'id' | 'created_at' | 'updated_at'>): Promise<Profile> {
    const result = await db.insert(profiles).values(profile).returning();
    return result[0];
  }

  async updateProfile(userId: string, updates: Partial<Profile>): Promise<Profile | undefined> {
    const result = await db.update(profiles)
      .set({ ...updates, updated_at: new Date() })
      .where(eq(profiles.user_id, userId))
      .returning();
    return result[0];
  }

  // Plans methods
  async getPlans(filters?: { status?: string; featured?: boolean }): Promise<Plan[]> {
    let conditions = [];

    if (filters?.status) {
      conditions.push(eq(plans.status, filters.status));
    }

    const result = await db.select().from(plans)
      .where(conditions.length ? and(...conditions) : undefined)
      .orderBy(desc(plans.featured), desc(plans.created_at));

    return result;
  }

  async getPlan(id: string): Promise<Plan | undefined> {
    const result = await db.select().from(plans).where(eq(plans.id, id)).limit(1);
    return result[0];
  }

  async createPlan(plan: Omit<Plan, 'id' | 'created_at' | 'updated_at'>): Promise<Plan> {
    const result = await db.insert(plans).values(plan).returning();
    return result[0];
  }

  async updatePlan(id: string, updates: Partial<Plan>): Promise<Plan | undefined> {
    const result = await db.update(plans)
      .set({ ...updates, updated_at: new Date() })
      .where(eq(plans.id, id))
      .returning();
    return result[0];
  }

  async deletePlan(id: string): Promise<boolean> {
    const result = await db.delete(plans).where(eq(plans.id, id));
    return result.length > 0;
  }

  // Orders methods
  async getOrders(userId?: string): Promise<Order[]> {
    const conditions = [];

    if (userId) {
      conditions.push(eq(orders.user_id, userId));
    }

    const result = await db.select().from(orders)
      .where(conditions.length ? and(...conditions) : undefined)
      .orderBy(desc(orders.created_at));

    return result;
  }

  async getOrder(id: string): Promise<Order | undefined> {
    const result = await db.select().from(orders).where(eq(orders.id, id)).limit(1);
    return result[0];
  }

  async createOrder(order: Omit<Order, 'id' | 'created_at' | 'updated_at'>): Promise<Order> {
    const result = await db.insert(orders).values({
      ...order,
      created_at: new Date(),
      updated_at: new Date(),
    }).returning();
    return result[0];
  }

  async updateOrder(id: string, updates: Partial<Order>): Promise<Order | undefined> {
    const result = await db.update(orders)
      .set({ ...updates, updated_at: new Date() })
      .where(eq(orders.id, id))
      .returning();
    return result[0];
  }

  // Downloads methods
  async getDownloads(userId?: string): Promise<Download[]> {
    const conditions = [];

    if (userId) {
      conditions.push(eq(downloads.user_id, userId));
    }

    const result = await db.select().from(downloads)
      .where(conditions.length ? and(...conditions) : undefined)
      .orderBy(desc(downloads.last_downloaded));

    return result;
  }

  async recordDownload(download: Omit<Download, 'id' | 'last_downloaded'>): Promise<Download> {
    const result = await db.insert(downloads).values({
      ...download,
      last_downloaded: new Date(),
    }).returning();
    return result[0];
  }

  // Analytics methods
  async getAnalytics(): Promise<any> {
    try {
      // Get total revenue from completed orders
      const revenueResult = await db.select({
        totalRevenue: sql<number>`SUM(${orders.amount})`.mapWith(Number),
        totalOrders: sql<number>`COUNT(*)`.mapWith(Number),
      }).from(orders).where(eq(orders.status, 'completed'));

      // Get total users count
      const usersResult = await db.select({
        totalUsers: sql<number>`COUNT(*)`.mapWith(Number),
      }).from(profiles);

      // Get total downloads count
      const downloadsResult = await db.select({
        totalDownloads: sql<number>`COUNT(*)`.mapWith(Number),
      }).from(downloads);

      // Get plan metrics
      const planMetricsResult = await db.select({
        packageType: orders.tier,
        count: sql<number>`COUNT(*)`.mapWith(Number),
      }).from(orders)
        .where(eq(orders.status, 'completed'))
        .groupBy(orders.tier);

      // Get recent activity (orders)
      const recentOrdersResult = await db.select().from(orders)
        .orderBy(desc(orders.created_at))
        .limit(10);

      // Get top plans by sales
      const topPlansResult = await db.select({
        planId: orders.plan_id,
        sales: sql<number>`COUNT(*)`.mapWith(Number),
        revenue: sql<number>`SUM(${orders.amount})`.mapWith(Number),
      }).from(orders)
        .where(eq(orders.status, 'completed'))
        .groupBy(orders.plan_id)
        .orderBy(desc(sql`COUNT(*)`))
        .limit(5);

      const totalRevenue = revenueResult[0]?.totalRevenue || 0;
      const totalOrders = revenueResult[0]?.totalOrders || 0;
      const totalUsers = usersResult[0]?.totalUsers || 0;
      const totalDownloads = downloadsResult[0]?.totalDownloads || 0;

      // Calculate plan metrics
      const planMetrics = {
        basicSales: planMetricsResult.find(p => p.packageType === 'basic')?.count || 0,
        standardSales: planMetricsResult.find(p => p.packageType === 'standard')?.count || 0,
        premiumSales: planMetricsResult.find(p => p.packageType === 'premium')?.count || 0,
        totalPlans: await db.select({ count: sql<number>`COUNT(*)`.mapWith(Number) }).from(plans).then(r => r[0]?.count || 0),
      };

      // Format recent activity
      const recentActivity = recentOrdersResult.map((order, index) => ({
        id: order.id,
        type: 'order' as const,
        description: `${order.tier} package purchased`,
        timestamp: new Date(order.created_at).toLocaleString(),
        amount: order.amount,
      }));

      // Get plan details for top plans
      const topPlans = await Promise.all(
        topPlansResult.map(async (topPlan) => {
          const plan = await this.getPlan(topPlan.planId);
          return {
            id: topPlan.planId,
            title: plan?.title || 'Unknown Plan',
            sales: topPlan.sales,
            revenue: topPlan.revenue,
            category: plan?.plan_type || 'Unknown',
          };
        })
      );

      return {
        overview: {
          totalRevenue,
          revenueGrowth: 12.5, // Mock growth percentage
          totalOrders,
          ordersGrowth: 8.2, // Mock growth percentage
          totalUsers,
          usersGrowth: 15.3, // Mock growth percentage
          totalDownloads,
          downloadsGrowth: 22.1, // Mock growth percentage
        },
        planMetrics,
        recentActivity,
        topPlans,
      };
    } catch (error) {
      console.error('Error getting analytics:', error);
      throw error;
    }
  }

  // Users methods
  async getAllUsers(): Promise<any[]> {
    try {
      const result = await db.select({
        id: profiles.id,
        userId: profiles.user_id,
        email: profiles.email,
        firstName: profiles.first_name,
        lastName: profiles.last_name,
        role: profiles.role,
        createdAt: profiles.created_at,
        updatedAt: profiles.updated_at,
      }).from(profiles).orderBy(desc(profiles.created_at));

      return result;
    } catch (error) {
      console.error('Error getting all users:', error);
      throw error;
    }
  }
}

export const storage = new DatabaseStorage();

