@tailwind base;
@tailwind components;
@tailwind utilities;

/* Modern Design System - Construction & Architecture Theme */
@layer base {
  :root {
    /* Core Colors */
    --background: 0 0% 100%;
    --foreground: 222 84% 5%;

    /* Card & Surface Colors */
    --card: 0 0% 100%;
    --card-foreground: 222 84% 5%;
    --popover: 0 0% 100%;
    --popover-foreground: 222 84% 5%;

    /* Primary Brand Colors - Modern Orange */
    --primary: 25 95% 53%;
    --primary-foreground: 0 0% 100%;
    --primary-light: 25 95% 95%;

    /* Secondary Colors - Professional Blue */
    --secondary: 217 91% 60%;
    --secondary-foreground: 0 0% 100%;
    --secondary-light: 217 91% 95%;

    /* Accent Colors */
    --accent: 142 76% 36%;
    --accent-foreground: 0 0% 100%;
    --accent-light: 142 76% 95%;

    /* Neutral Colors */
    --muted: 220 14% 96%;
    --muted-foreground: 220 9% 46%;
    --border: 220 13% 91%;
    --input: 220 13% 91%;
    --ring: 25 95% 53%;

    /* Success & Warning */
    --success: 142 76% 36%;
    --success-foreground: 0 0% 100%;
    --warning: 38 92% 50%;
    --warning-foreground: 0 0% 100%;

    /* Destructive */
    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    /* Border Radius */
    --radius: 1rem;

    /* Construction Theme Specific */
    --construction-gold: 45 93% 47%;
    --construction-gold-light: 45 93% 95%;
    --construction-steel: 220 14% 96%;
    --construction-steel-dark: 220 14% 20%;

    /* Enhanced Gradients */
    --gradient-primary: linear-gradient(135deg, hsl(25 95% 53%), hsl(20 95% 48%));
    --gradient-secondary: linear-gradient(135deg, hsl(217 91% 60%), hsl(217 91% 50%));
    --gradient-success: linear-gradient(135deg, hsl(142 76% 36%), hsl(142 76% 30%));
    --gradient-hero: linear-gradient(135deg, hsl(25 95% 53%), hsl(20 90% 45%));
    --gradient-card: linear-gradient(145deg, hsl(0 0% 100%), hsl(220 14% 98%));
    --gradient-glass: linear-gradient(135deg, rgba(255,255,255,0.1), rgba(255,255,255,0.05));

    /* Enhanced Shadows */
    --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
    --shadow: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
    --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
    --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
    --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
    --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
    --shadow-construction: 0 20px 40px -10px hsl(25 95% 53% / 0.15);
    --shadow-glow: 0 0 20px hsl(25 95% 53% / 0.3);

    /* Glass Effect */
    --glass-bg: rgba(255, 255, 255, 0.1);
    --glass-border: rgba(255, 255, 255, 0.2);
    --glass-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37);

