    --sidebar-foreground: 220 9% 46%;
    --sidebar-primary: 220 14% 20%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 220 14% 95%;
    --sidebar-accent-foreground: 220 14% 20%;
