    --secondary-light: 217 91% 15%;

    --accent: 142 76% 36%;
    --accent-foreground: 222 84% 5%;
    --accent-light: 142 76% 15%;

    --muted: 217 33% 17%;
    --muted-foreground: 215 20% 65%;
    --border: 217 33% 17%;
    --input: 217 33% 17%;
    --ring: 25 95% 53%;

    --success: 142 76% 36%;
    --success-foreground: 222 84% 5%;
    --warning: 38 92% 50%;
    --warning-foreground: 222 84% 5%;

    --destructive: 0 63% 31%;
    --destructive-foreground: 210 40% 98%;

    --construction-gold: 45 93% 47%;
    --construction-gold-light: 45 93% 15%;
    --construction-steel: 217 33% 17%;
    --construction-steel-dark: 220 14% 80%;

    --glass-bg: rgba(0, 0, 0, 0.1);
    --glass-border: rgba(255, 255, 255, 0.1);
    --glass-shadow: 0 8px 32px 0 rgba(0, 0, 0, 0.37);

    --sidebar-background: 222 84% 5%;
