    --sidebar-primary: 210 40% 98%;
    --sidebar-primary-foreground: 222 84% 5%;
    --sidebar-accent: 217 33% 17%;
    --sidebar-accent-foreground: 210 40% 98%;
    --sidebar-border: 217 33% 17%;
    --sidebar-ring: 25 95% 53%;
  }
}

/* Custom Component Styles */
@layer components {
  /* Glass Morphism */
  .glass {
    background: var(--glass-bg);
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border: 1px solid var(--glass-border);
    box-shadow: var(--glass-shadow);
  }

  /* Modern Cards */
  .card-modern {
    @apply bg-card border border-border/50 rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300;
    background: linear-gradient(145deg, hsl(var(--card)), hsl(var(--card) / 0.8));
  }

  /* Gradient Text */
  .gradient-text {
    @apply bg-gradient-to-r from-primary via-primary to-secondary bg-clip-text text-transparent;
  }

  /* Animated Border */
  .animated-border {
    position: relative;
    background: linear-gradient(90deg, hsl(var(--primary)), hsl(var(--secondary)), hsl(var(--accent)));
    padding: 2px;
    border-radius: var(--radius);
  }

  .animated-border > * {
    @apply bg-background rounded-[calc(var(--radius)-2px)];
  }

  /* Floating Animation */
  .float {
    animation: float 6s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
  }

  /* Pulse Glow */
  .pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite alternate;
  }

  @keyframes pulse-glow {
    from { box-shadow: 0 0 20px hsl(var(--primary) / 0.3); }
    to { box-shadow: 0 0 30px hsl(var(--primary) / 0.6); }
  }

  /* Modern Button Styles */
  .btn-primary {
    @apply bg-gradient-to-r from-primary to-primary text-primary-foreground font-semibold px-6 py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105;
  }

  .btn-secondary {
    @apply bg-gradient-to-r from-secondary to-secondary-foreground text-white font-semibold px-6 py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105;
  }

  .btn-outline-modern {
    @apply border-2 border-primary/20 bg-background/80 backdrop-blur-sm text-primary hover:bg-primary hover:text-primary-foreground font-semibold px-6 py-3 rounded-xl transition-all duration-300 transform hover:scale-105;
  }

  /* Modern Input Styles */
  .input-modern {
    @apply bg-background/50 border border-border/50 rounded-xl px-4 py-3 focus:ring-2 focus:ring-primary/20 focus:border-primary transition-all duration-300 backdrop-blur-sm;
  }

  /* Stats Cards */
  .stat-card {
    @apply bg-gradient-to-br from-card to-card/80 border border-border/30 rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 backdrop-blur-sm;
  }

  /* Hero Section */
  .hero-gradient {
    background: linear-gradient(135deg, hsl(var(--primary) / 0.1), hsl(var(--secondary) / 0.1), hsl(var(--accent) / 0.1));
  }

  /* Admin Dashboard */
  .admin-card {
    @apply bg-gradient-to-br from-card via-card to-muted/30 border border-border/50 rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300;
  }

  .metric-card {
    @apply bg-gradient-to-br from-primary/5 via-primary/10 to-primary/5 border border-primary/20 rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300;
  }
}

/* Utility Classes */
@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }
}

