import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Separator } from '@/components/ui/separator';
import { 
  User, 
  Heart, 
  ShoppingBag, 
  Settings, 
  Download, 
  Star, 
  Calendar,
  MapPin,
  Phone,
  Mail,
  Edit,
  Plus,
  ArrowRight,
  Home,
  FileText,
  CreditCard
} from 'lucide-react';
import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '@/hooks/useAuth';
import villaImage from '@/assets/villa-plan.jpg';
import bungalowImage from '@/assets/bungalow-plan.jpg';
import townhouseImage from '@/assets/townhouse-plan.jpg';
import UserHeader from '@/components/UserHeader';

const UserDashboard = () => {
  const { user, profile, signOut } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  const [userStats, setUserStats] = useState({
    totalOrders: 0,
    totalSpent: 0,
    favoritePlans: 0,
    downloads: 0
  });
  const [recentOrders, setRecentOrders] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      fetchUserData();
    }
  }, [user]);

  const fetchUserData = async () => {
    if (!user) return;

    try {
      // Fetch user analytics from API
      const analyticsResponse = await fetch(`/api/analytics/user/${user.id}`);
      if (!analyticsResponse.ok) throw new Error('Failed to fetch user analytics');

      const analytics = await analyticsResponse.json();

      setUserStats({
        totalOrders: analytics.totalOrders || 0,
        totalSpent: analytics.totalSpent || 0,
        favoritePlans: 0, // TODO: Implement favorites
        downloads: analytics.totalDownloads || 0
      });

      setRecentOrders(analytics.recentOrders || []);
    } catch (error) {
      console.error('Error fetching user data:', error);
    } finally {
      setLoading(false);
    }
  };


  const favoritePlans = [
    {
      id: 1,
      title: 'Luxury Villa Paradise',
      type: 'Villa',
      bedrooms: 5,
      bathrooms: 4,
      area: 3200,
      price: 4500,
      image: villaImage
    },
    {
      id: 7,
      title: 'Modern Family Bungalow',
      type: 'Bungalow',
      bedrooms: 3,
      bathrooms: 2,
      area: 1800,
      price: 3100,
      image: bungalowImage
    },
    {
      id: 13,
      title: 'Contemporary Townhouse',
      type: 'Townhouse',
      bedrooms: 4,
      bathrooms: 3,
      area: 2400,
      price: 3800,
      image: townhouseImage
    }
  ];

  const recentActivity = [
    {
      type: 'purchase',
      message: 'Purchased Luxury Villa Paradise - Premium Package',
      date: '2024-01-15',
      amount: 4500
    },
    {
      type: 'favorite',
      message: 'Added Modern Family Bungalow to favorites',
      date: '2024-01-12'
    },
    {
      type: 'download',
      message: 'Downloaded Contemporary Townhouse plans',
      date: '2024-01-10'
    },
    {
      type: 'purchase',
      message: 'Purchased Modern Family Bungalow - Standard Package',
      date: '2024-01-10',
      amount: 2300
    }
  ];

  useEffect(() => {
    if (!user) {
      navigate('/');
    }
  }, [user, navigate]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  // Use mock profile data if real profile is not available
  const userProfile = profile || {
    id: 'mock-id',
    user_id: user?.id || '',
    email: user?.email || '',
    first_name: 'John',
    last_name: 'Doe',
    role: 'user' as const,
    avatar_url: undefined
  };

  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'processing':
        return 'bg-yellow-100 text-yellow-800';
      case 'pending':
        return 'bg-blue-100 text-blue-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'purchase':
        return <ShoppingBag className="h-4 w-4" />;
      case 'favorite':
        return <Heart className="h-4 w-4" />;
      case 'download':
        return <Download className="h-4 w-4" />;
      default:
        return <FileText className="h-4 w-4" />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-background to-construction-gray-light">
      <UserHeader 
        title={`Welcome back, ${userProfile.first_name || 'User'}!`}
        subtitle="Manage your account, view orders, and explore plans"
        showBackButton={false}
        showUserInfo={false}
        actions={
          <div className="flex items-center gap-4">
            <Button variant="outline" asChild>
              <Link to="/plans">
                <Plus className="h-4 h-4 mr-2" />
                Browse Plans
              </Link>
            </Button>
            <Button variant="outline" onClick={signOut}>
              Sign Out
            </Button>
          </div>
        }
      />

      {/* Dashboard Content */}
      <section className="py-16">
        <div className="container px-4">
          <div className="max-w-7xl mx-auto">
            <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-8">
              <TabsList className="grid w-full grid-cols-4">
                <TabsTrigger value="overview">Overview</TabsTrigger>
                <TabsTrigger value="orders">Orders</TabsTrigger>
                <TabsTrigger value="favorites">Favorites</TabsTrigger>
                <TabsTrigger value="activity">Activity</TabsTrigger>
              </TabsList>

              <TabsContent value="overview" className="space-y-8">
                {/* Stats Cards */}
                <div className="grid md:grid-cols-4 gap-6">
                  <Card>
                    <CardContent className="p-6">
                      <div className="flex items-center gap-4">
                        <div className="p-3 bg-primary/10 rounded-lg">
                          <ShoppingBag className="h-6 w-6 text-primary" />
                        </div>
                        <div>
                          <p className="text-sm text-muted-foreground">Total Orders</p>
                          <p className="text-2xl font-bold">{userStats.totalOrders}</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardContent className="p-6">
                      <div className="flex items-center gap-4">
                        <div className="p-3 bg-green-500/10 rounded-lg">
                          <CreditCard className="h-6 w-6 text-green-500" />
                        </div>
                        <div>
                          <p className="text-sm text-muted-foreground">Total Spent</p>
                          <p className="text-2xl font-bold">â‚µ{userStats.totalSpent.toLocaleString()}</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardContent className="p-6">
                      <div className="flex items-center gap-4">
                        <div className="p-3 bg-red-500/10 rounded-lg">
                          <Heart className="h-6 w-6 text-red-500" />
                        </div>
                        <div>
                          <p className="text-sm text-muted-foreground">Favorite Plans</p>
                          <p className="text-2xl font-bold">{userStats.favoritePlans}</p>
                        </div>
                      </div>
                    </CardContent>
