import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Separator } from '@/components/ui/separator';
import { Badge } from '@/components/ui/badge';
import { 
  ArrowLeft, 
  Settings, 
  Bell, 
  Shield, 
  Eye, 
  EyeOff,
  Lock,
  Mail,
  Globe,
  Palette,
  Smartphone,
  CreditCard,
  Trash2,
  Download,
  Upload,
  Save,
  X,
  Check,
  AlertTriangle,
  Info
} from 'lucide-react';
import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '@/hooks/useAuth';
import UserHeader from '@/components/UserHeader';

const UserSettings = () => {
  const { user, profile, signOut } = useAuth();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const [settings, setSettings] = useState({
    // Notifications
    emailNotifications: true,
    smsNotifications: false,
    marketingEmails: true,
    orderUpdates: true,
    newPlans: false,
    priceAlerts: true,

    // Privacy
    profileVisibility: 'public',
    showEmail: false,
    showPhone: false,
    allowMessages: true,

    // Preferences
    language: 'en',
    currency: 'GHS',
    timezone: 'Africa/Accra',
    theme: 'system',

    // Security
    twoFactorAuth: false,
    loginAlerts: true,
    sessionTimeout: '24h',

    // Data
    autoBackup: true,
    dataRetention: '2years'
  });

  const [passwordForm, setPasswordForm] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  useEffect(() => {
    if (!user) {
      navigate('/');
    }
  }, [user, navigate]);

  // Use mock profile data if real profile is not available
  const userProfile = profile || {
    id: 'mock-id',
    user_id: user?.id || '',
    email: user?.email || '',
    first_name: 'John',
    last_name: 'Doe',
    role: 'user' as const,
    avatar_url: undefined
  };

  const handleSettingChange = (key: string, value: any) => {
    setSettings(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const handlePasswordChange = (field: string, value: string) => {
    setPasswordForm(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const saveSettings = async () => {
    setIsLoading(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      console.log('Settings saved:', settings);
      // You could show a success toast here
    } catch (error) {
      console.error('Error saving settings:', error);
      // You could show an error toast here
    } finally {
      setIsLoading(false);
    }
  };

  const changePassword = async () => {
    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      alert('New passwords do not match');
      return;
    }

    setIsLoading(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      console.log('Password changed');
      setPasswordForm({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });
      // You could show a success toast here
    } catch (error) {
      console.error('Error changing password:', error);
      // You could show an error toast here
    } finally {
      setIsLoading(false);
    }
  };

  const exportData = () => {
    const data = {
      profile: userProfile,
      settings,
      orders: [], // This would be fetched from your backend
      favorites: [] // This would be fetched from your backend
    };

    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `user-data-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const deleteAccount = () => {
    if (confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
      // Handle account deletion
      console.log('Account deletion requested');
    }
  };

  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading settings...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-background to-construction-gray-light">
      <UserHeader 
        title="Account Settings"
        subtitle="Manage your account preferences and security"
        actions={
          <Button onClick={saveSettings} disabled={isLoading}>
            {isLoading ? (
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
            ) : (
              <Save className="h-4 w-4 mr-2" />
            )}
            Save Changes
          </Button>
        }
      />

      {/* Settings Content */}
      <section className="py-16">
        <div className="container px-4">
          <div className="max-w-6xl mx-auto">
            <div className="grid lg:grid-cols-3 gap-8">
              {/* Settings Navigation */}
              <div className="lg:col-span-1">
                <Card>
                  <CardHeader>
                    <CardTitle>Settings</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <nav className="space-y-2">
                      <a href="#notifications" className="flex items-center gap-3 p-3 rounded-lg hover:bg-muted transition-colors">
                        <Bell className="h-4 w-4" />
                        <span>Notifications</span>
                      </a>
                      <a href="#privacy" className="flex items-center gap-3 p-3 rounded-lg hover:bg-muted transition-colors">
                        <Eye className="h-4 w-4" />
                        <span>Privacy</span>
                      </a>
                      <a href="#preferences" className="flex items-center gap-3 p-3 rounded-lg hover:bg-muted transition-colors">
                        <Palette className="h-4 w-4" />
                        <span>Preferences</span>
                      </a>
                      <a href="#security" className="flex items-center gap-3 p-3 rounded-lg hover:bg-muted transition-colors">
                        <Shield className="h-4 w-4" />
                        <span>Security</span>
                      </a>
                      <a href="#data" className="flex items-center gap-3 p-3 rounded-lg hover:bg-muted transition-colors">
                        <Download className="h-4 w-4" />
                        <span>Data & Export</span>
                      </a>
                    </nav>
                  </CardContent>
                </Card>
              </div>

              {/* Settings Forms */}
              <div className="lg:col-span-2 space-y-8">
                {/* Notifications */}
                <Card id="notifications">
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <Bell className="h-5 w-5" />
                      Notifications
                    </CardTitle>
                    <CardDescription>
                      Manage how you receive notifications and updates
                    </CardDescription>
                  </CardHeader>
                  <CardContent className="space-y-6">
                    <div className="space-y-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <Label htmlFor="email-notifications">Email Notifications</Label>
                          <p className="text-sm text-muted-foreground">Receive notifications via email</p>
                        </div>
                        <Switch
                          id="email-notifications"
                          checked={settings.emailNotifications}
                          onCheckedChange={(checked) => handleSettingChange('emailNotifications', checked)}
                        />
                      </div>

                      <div className="flex items-center justify-between">
                        <div>
                          <Label htmlFor="sms-notifications">SMS Notifications</Label>
                          <p className="text-sm text-muted-foreground">Receive notifications via SMS</p>
                        </div>
                        <Switch
                          id="sms-notifications"
                          checked={settings.smsNotifications}
                          onCheckedChange={(checked) => handleSettingChange('smsNotifications', checked)}
                        />
                      </div>

                      <div className="flex items-center justify-between">
                        <div>
                          <Label htmlFor="marketing-emails">Marketing Emails</Label>
                          <p className="text-sm text-muted-foreground">Receive promotional emails and offers</p>
                        </div>
                        <Switch
                          id="marketing-emails"
                          checked={settings.marketingEmails}
                          onCheckedChange={(checked) => handleSettingChange('marketingEmails', checked)}
                        />
                      </div>

                      <div className="flex items-center justify-between">
                        <div>
                          <Label htmlFor="order-updates">Order Updates</Label>
                          <p className="text-sm text-muted-foreground">Get notified about order status changes</p>
                        </div>
                        <Switch
                          id="order-updates"
                          checked={settings.orderUpdates}
                          onCheckedChange={(checked) => handleSettingChange('orderUpdates', checked)}
                        />
                      </div>

                      <div className="flex items-center justify-between">
                        <div>
                          <Label htmlFor="new-plans">New Plans</Label>
                          <p className="text-sm text-muted-foreground">Get notified when new house plans are added</p>
